import React, { Component } from 'react';
import * as cloneDeep from 'lodash/cloneDeep';
import { Route } from 'react-router-dom';
import PhotoBrowser from './components/PhotoBrowser.js';
import Home from './components/Home.js';
import About from './components/About.js';


class App extends Component {
  constructor(props) {
    super(props);
    // temp backup copy of photos
    this.state = { photos: [], favorites: [], temp: [] };
  }

  async componentDidMount() {
    try {
      const url = "http://randyconnolly.com/funwebdev/services/travel/images.php";
      const response = await fetch(url);
      const jsonData = await response.json();
      this.setState( { photos: jsonData, temp: [] } );
    }
    catch (error) {
      console.error(error);
    }
  }

  render() {
    return (
      <div>
        <Route path='/' exact component={Home} />
        <Route path='/home' exact component={Home} />
        <Route path='/browse' exact 
          render={ (props) => 
          <PhotoBrowser
            uniqueCity={ this.uniqueCity()}
            uniqueCountry={ this.uniqueCountry()}
            filter={ this.filter}
            favorites={ this.state.favorites} 
            photos={ this.state.photos } 
            updatePhoto={ this.updatePhoto }  
            addPhotoToFavorites={ this.addPhotoToFavorites } />
           }
        />
        <Route path='/about' exact component={About} />
      </div>

    );
  }

  uniqueCity = () => {
    const cities = this.state.photos.map( (p) => p.city);
    // creates a unique array of cities returning only unique ones
    const uniqueCities = [...new Set(cities)];
    // console.log(uniqueCities);
    return uniqueCities;
  }

  uniqueCountry = () => {
    const country = this.state.temp.map( (p) => p.country);
    // creates a unique array of countries returning only unique ones
    const uniqueCountry = [...new Set(country)];
    // console.log(uniqueCountry);
    return uniqueCountry;
}



  updatePhoto = (id, photo) => {
    // Create a deep clone of photo array from state.
    // We will use a lodash function for that task.
    const copyPhotos = cloneDeep(this.state.photos);

    // find photo to update in cloned array
    const photoToReplace = copyPhotos.find( p => p.id === id);

    // replace photo fields with edited values
    photoToReplace.title = photo.title;
    photoToReplace.city = photo.city;
    photoToReplace.country = photo.country;

    // update state
    this.setState( { photos: copyPhotos } );
  }

  addPhotoToFavorites = (id) => {
    // find photo to add
    const photo = this.state.photos.find ( p => p.id === id);
    console.log(photo);

    // check if item is already in favorite
    // if not add it
    if (!this.state.favorites.find (p => p.id === id) ) {
      // create copy of favorites
      const copyFavorites = cloneDeep(this.state.favorites);

      // push item into array
      copyFavorites.push(photo);

      // update state
      this.setState( { favorites: copyFavorites });
    } else {
      console.log ("Photo already in favorites")
    }
  }

  filter = (name, value) => {
    console.log("filter invoked");
    const tmp = cloneDeep(this.state.photos);
    if (name === "city") {
      console.log("city conditional invoked");
      console.log(value);
      if (value !== "default") {
        console.log("not default");
        const filtered = tmp.filter(obj => obj.city === value);
        console.log(filtered);
        this.setState({photos: filtered});
      } else {
        this.setState({photos: this.state.temp});
      }
    } else {
      if (value !== "default") {
        tmp.filter(obj => obj.country === value);
        this.setState({photos: tmp});
      } else {
        this.setState({photos: this.state.temp});
      }
    }
  }


}

export default App;
