import React, { Component } from 'react';
import HeaderApp from './components/HeaderApp.js';
import PhotoBrowser from './components/PhotoBrowser.js';
import * as cloneDeep from 'lodash/cloneDeep';
import { Route } from 'react-router-dom';
import * as JSZipUtils from 'jszip-utils';
import * as JSZip from 'jszip';
import FileSaver from 'file-saver';
import Home from './components/Home.js';
import About from './components/About.js';
import Favourites from './components/Favourites.js';


function urlToPromise(url) {
	return new Promise(function(resolve, reject) {
		JSZipUtils.getBinaryContent(url, function (err, data) {
			if(err) {
				reject(err);
			} else {
				resolve(data);
			}
		});
	});
}


class App extends Component {

	constructor(props) {
		super(props);
		this.state = { photos: [], favourites: [], filteredPhotos: [], cityFilter: '', countryFilter: '' };
		const cachedFaves = localStorage.getItem('favourites');
	}

	downloadFaves = () => {

		const favePhotos = this.state.photos.filter((p) => this.state.favourites.includes(p.id));
		const imgURL = 'https://storage.googleapis.com/funwebdev-3rd-travel/large/';
		const proxyURL = 'https://cors-anywhere.herokuapp.com/';

		var zip = new JSZip();

		favePhotos.forEach((p) => {
			let filePath = proxyURL + imgURL + p.path;
			zip.file(`Travel Image ${p.id}.jpg`, urlToPromise(filePath), {binary:true});
			console.log(`Zipped Travel Image ${p.id}.jpg`);
		});

		zip.generateAsync({type:"blob"})
			.then(function callback(blob) {

				// see FileSaver.js
				FileSaver.saveAs(blob, "faveImages.zip");
			});
	}

	addToFavourites = (id) => {

		if (!this.state.favourites.includes(id)){
			localStorage.setItem('favourites', [...this.state.favourites, id]);
			this.setState(prevState => ({
				favourites: [...prevState.favourites, id]
			}));
		}
	}

	filterCity = (city) => {

		console.log(city);
		this.setState( {cityFilter: city} );

		this.filterPhotos();
	}

	filterCountry = (country) => {

		console.log(country);
		this.setState( {countryFilter: country} );
		console.log(this.state.countryFilter);
		this.filterPhotos();
	}

	filterPhotos = () => {

		let filteredPhotos = this.state.photos;

		filteredPhotos = this.state.photos.filter(p => this.matchesCity(p) && this.matchesCountry(p));

		this.setState( {filteredPhotos: filteredPhotos} );
		//console.log(filteredPhotos);
	}

	matchesCity = (p) => {
		let matches;

		if(this.state.cityFilter.length > 0) {
			matches = p.city.toUpperCase().includes(this.state.cityFilter.toUpperCase());
		} else {
			matches = true;
		}

		console.log(`City: ${p.city}, City Filter: ${this.state.cityFilter}, Matches: ${matches}`);

		return matches;
	}	

	matchesCountry = (p) => {
		let matches;

		if(this.state.countryFilter.length > 0) {
			matches = p.country.toUpperCase().includes(this.state.countryFilter.toUpperCase());
		} else {
			matches = true;
		}

		console.log(`Country: ${p.country}, Country Filter: ${this.state.countryFilter}, Matches: ${matches}`);

		return matches;
	}

	removeFromFavourites = (id) => {

		let oldFaves = [...this.state.favourites];
		let index = oldFaves.indexOf(id);
		oldFaves.splice(index, 1);
		this.setState({favourites: oldFaves});

		localStorage.setItem('favourites', oldFaves);
	}

	updatePhoto = (id, photo) => {

		console.log("changed in app");

		// Create deep clone of photo array from state using lodash
		const copyPhotos = cloneDeep(this.state.photos);

		// Find photo to update in cloned array
		const photoToReplace = copyPhotos.find( p => p.id === id);

		// replace photo fields with edited values
		photoToReplace.title = photo.title;
		photoToReplace.city = photo.city;
		photoToReplace.country = photo.country;

		//update state
		this.setState( {photos: copyPhotos} );
	}

	async componentDidMount() {
		try {
			const url = "http://randyconnolly.com/funwebdev/services/travel/images.php";
			const response = await fetch(url);
			const jsonData = await response.json();
			this.setState( {photos: jsonData} );
			this.setState( {filteredPhotos: jsonData} );
			// if (cachedFaves) {
				// this.setState({ favourites: cachedFaves });
			// }
		}
		catch (error) {
			console.error(error);
		}
	}

	render() {
		return (
			<div>
				<HeaderApp />
				<Route path='/' exact component={Home} />
				<Route path='/home' exact component={Home} />
				<Route path='/about' exact component={About} />
				<Route path='/browse' exact 
					render={ (props) => 
						<div>
							<Favourites
								photos={this.state.filteredPhotos}
								favourites={this.state.favourites}
								removeFromFavourites={this.removeFromFavourites}
								downloadFaves={this.downloadFaves}
							/>
							<PhotoBrowser
								photos={this.state.photos}
								updatePhoto={this.updatePhoto}
								addToFavourites={this.addToFavourites} 
								filterCountry={this.filterCountry}
								filterCity={this.filterCity}
							/>
						</div>
					}
				/>
			</div>
		);
	}
}

export default App;
