import React from 'react';
import PhotoThumb from './PhotoThumb.js';

class PhotoList extends React.Component {
	
	render() {
		
		let photos = [...this.props.photos];
		
		if (this.props.photos.length > 1) {
			return (
				<article className="photos">
					<form className="">
						<legend>Filter Photos</legend>
						<label>Country</label>
						<input
							type='text' 
							name='country'
							onChange={this.handleCountryFilter}
						/>
						<label>City</label>
						<input
							type='text' 
							name='city' 
							onChange={this.handleCityFilter}
						/>
					</form>
					{ this.props.photos.map( (p) => 
						<PhotoThumb
							photo={p}
							key={p.id}
							showImageDetails={this.props.showImageDetails} 
							addToFavourites={this.props.addToFavourites}
						/> ) }
				</article>
			);
		} else {
			return null;
		}
	}
}

export default PhotoList;
