import React from 'react';
import PhotoList from './PhotoList';
import EditPhotoDetails from './EditPhotoDetails';
import HeaderApp from './HeaderApp.js';
import Favorites from './Favorites.js';

class PhotoBrowser extends React.Component {
    constructor(props) {
        super(props);
        this.state = { currentPhoto: 1};
    }

    handleFilter = (e) => {
        console.log(e.target.value);
        this.props.filter("city", e.target.value)
    }

    createOptionCity = () => {
        const cities = this.props.photos.map( (p) => p.city);
        // creates a unique array of cities returning only unique ones
        const uniqueCities = [...new Set(cities)];
        // console.log(uniqueCities);
        return uniqueCities;
    }

    createOptionCountry = () => {
        const country = this.props.photos.map( (p) => p.country);
        // creates a unique array of countries returning only unique ones
        const uniqueCountry = [...new Set(country)];
        // console.log(uniqueCountry);
        return uniqueCountry;
    }

    render() {
        return (
            <div>
                <HeaderApp />
                <Favorites favorites={this.props.favorites} />
                <div className="photoBrowser">
                    <div className="sorting">
                            <label>Filter by: </label>
                            <label>City: </label>
                            <select name="city" onChange={this.handleFilter} >
                                <option value="default" >default</option>
                                {this.createOptionCity().map( (p) => <option key={p} value={p}>{p}</option>)}
                            </select>
                            <label> Country: </label>
                            <select name="country">
                                <option value="default" >default</option>
                                {this.createOptionCountry().map( (p) => <option key={p} value={p}>{p}</option>)}
                            </select>
                    </div>
                    <section className="container">
                        <PhotoList photos={ this.props.photos } showImageDetails={ this.showImageDetails } addPhotoToFavorites={ this.props.addPhotoToFavorites } />
                        <EditPhotoDetails photos={ this.props.photos } currentPhoto={ this.state.currentPhoto } updatePhoto={ this.props.updatePhoto } />
                    </section>
                </div>
            </div>
        );
    }

    showImageDetails = (id) => {
        this.setState( {currentPhoto: id });
    }
}

export default PhotoBrowser;
